import React, { useState, useEffect } from "react";
import { Link, useParams, useNavigate } from "react-router-dom";
import FormComponent from "./FormComponent"; // Assuming this is your form component

function AddCard() {
  const { deckId } = useParams();
  const navigate = useNavigate();

  const [front, setFront] = useState("");
  const [back, setBack] = useState("");
  const [deck, setDeck] = useState({});

  useEffect(() => {
    async function loadDeck() {
      try {
        const response = await fetch(`http://mockhost/decks/${deckId}`);
        const fetchedDeck = await response.json();
        setDeck(fetchedDeck);
      } catch (error) {
        console.error("Error fetching deck", error);
        navigate("/");
      }
    }
    loadDeck();
  }, [deckId, navigate]);

  const handleFrontChange = (event) => setFront(event.target.value);
  const handleBackChange = (event) => setBack(event.target.value);

  const handleSaveButton = async (event) => {
    event.preventDefault();
    console.log("Submit button clicked"); // Make sure this gets triggered

    if (!front || !back) {
      alert("Both front and back fields must be filled out.");
      return;
    }

    try {
      const response = await fetch(`http://mockhost/decks/${deckId}/cards`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ front, back }),
      });

      if (!response.ok) {
        throw new Error("Failed to save the card");
      }

      setFront("");
      setBack("");
      navigate(`/decks/${deckId}`);
    } catch (error) {
      console.error("Error saving the card:", error);
    }
  };

  return (
    <>
      <div className="container my-3">
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <Link to="/" style={{ color: "black" }}>Home</Link>
            </li>
            <li className="breadcrumb-item">
              <Link to={`/decks/${deckId}`} style={{ color: "black" }}>{deck.name}</Link>
            </li>
            <li className="breadcrumb-item active" aria-current="page">
              Add Card
            </li>
          </ol>
        </nav>

        <div className="card p-4" style={{ backgroundColor: "#F5F5DC", borderRadius: "10px" }}>
          <h2 className="mb-4" style={{ color: "#3B2F2F" }}>Create Card</h2>
          {/* Passing the handlers and values to FormComponent */}
          <FormComponent
            front={front}
            back={back}
            onFrontChange={handleFrontChange}
            onBackChange={handleBackChange}
            onSubmit={handleSaveButton}
            onCancel={() => navigate(`/decks/${deckId}`)}
            isEditMode={false}
          />
        </div>
      </div>
    </>
  );
}

export default AddCard;
