import { useState, useEffect } from "react";
import { Link, useParams, useNavigate } from "react-router-dom";
import FormComponent from "./FormComponent";

function AddCard() {
  const { deckId } = useParams();
  const navigate = useNavigate();

  const [deck, setDeck] = useState({});
  const [front, setFront] = useState("");
  const [back, setBack] = useState("");

  useEffect(() => {
    async function loadDeck() {
      try {
        const response = await fetch(`http://mockhost/decks/${deckId}`);
        if (response.status === 404) {
          navigate("/");
          return;
        }
        const fetchedDeck = await response.json();
        setDeck(fetchedDeck);
      } catch (error) {
        console.log("There was an error fetching the deck", error);
        navigate("/");
      }
    }
    loadDeck();
  }, [deckId, navigate]);
  const handleSaveButton = async (event) => {
    event.preventDefault();

    // Check that front and back fields are filled out
    if (!front || !back) {
      console.error("Both front and back fields are required.");
      return;
    }

    try {
      const response = await fetch(`http://mockhost/decks/${deckId}/cards`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ front, back }), // Send front and back in request body
      });

      // If response is not OK (HTTP 4xx/5xx), throw an error
      if (!response.ok) {
        const errorData = await response.json(); // Parse the response as JSON
        console.error("Error response data:", errorData); // Log the error data for debugging

        // If there's a specific error message, show it, else fallback to a generic message
        const errorMessage = errorData.message || JSON.stringify(errorData);
        throw new Error(`Failed to save the card: ${errorMessage}`);
      }

      // If successful, parse the JSON response
      const savedCard = await response.json();
      console.log("Card saved:", savedCard);

      // Clear the form after saving
      setFront("");
      setBack("");

      // Navigate back to the deck
      navigate(`/decks/${deckId}`);
    } catch (error) {
      // Log the error message properly
      console.error("Error saving the card:", error.message);
    }
  };


  return (
    <>
      <div className="container my-3">
        {/* Breadcrumb Navigation */}
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <Link to="/" style={{ color: 'black' }}>Home</Link>
            </li>
            <li className="breadcrumb-item">
              <Link to={`/decks/${deckId}`} style={{ color: 'black' }}>{deck.name}</Link>
            </li>
            <li className="breadcrumb-item active" aria-current="page">
              Add Card
            </li>
          </ol>
        </nav>

        {/* Use FormComponent */}
        <div className="card p-4" style={{ backgroundColor: '#F5F5DC', borderRadius: '10px' }}>
          <h2 className="mb-4" style={{ color: '#3B2F2F' }}>Create Card</h2>
          <FormComponent
            front={front}
            back={back}
            onFrontChange={(e) => setFront(e.target.value)} // Update front state on change
            onBackChange={(e) => setBack(e.target.value)}   // Update back state on change
            onSubmit={handleSaveButton}                    // Handle form submission
            onCancel={() => navigate(`/decks/${deckId}`)}   // Handle form cancellation
            isEditMode={false}
          />
        </div>
      </div>
    </>
  );
}

export default AddCard;
