import { useState, useEffect } from "react";
import { Link, useParams, useNavigate } from "react-router-dom";
import FormComponent from "./FormComponent";

function AddCard() {
  const { deckId } = useParams(); // Get the deckId from the URL
  const navigate = useNavigate();

  const [deck, setDeck] = useState({});
  const [front, setFront] = useState(""); // State for the front of the card
  const [back, setBack] = useState(""); // State for the back of the card

  // Handle changes in the front and back fields
  const handleFrontChange = (event) => setFront(event.target.value);
  const handleBackChange = (event) => setBack(event.target.value);

  // Fetch the deck details when the component mounts
  useEffect(() => {
    async function loadDeck() {
      try {
        const response = await fetch(`http://mockhost/decks/${deckId}`);
        if (response.status === 404) {
          navigate("/"); // Redirect to home if the deck is not found
          return;
        }
        const fetchedDeck = await response.json();
        setDeck(fetchedDeck);
      } catch (error) {
        console.log("There was an error fetching the deck", error);
        navigate("/"); // Redirect on error
      }
    }
    loadDeck();
  }, [deckId, navigate]);

  // Handle form submission for saving a new card
  const handleSaveButton = async (event) => {
    event.preventDefault(); // Prevent default form submission
    try {
      const response = await fetch(`http://mockhost/decks/${deckId}/cards`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          front, // Front of the card
          back,  // Back of the card
        }),
      });
      if (!response.ok) throw new Error("Failed to save the card");

      // Clear the form fields after saving
      setFront("");
      setBack("");

      // Navigate back to the deck view
      navigate(`/decks/${deckId}`);
    } catch (error) {
      console.error("Error saving the card:", error);
    }
  };

  return (
    <>
      <div className="container my-3">
        {/* Breadcrumb Navigation */}
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <Link to="/" style={{ color: 'black' }}>Home</Link>
            </li>
            <li className="breadcrumb-item">
              <Link to={`/decks/${deckId}`} style={{ color: 'black' }}>{deck.name}</Link>
            </li>
            <li className="breadcrumb-item active" aria-current="page">
              Add Card
            </li>
          </ol>
        </nav>

        {/* FormComponent for adding a new card */}
        <div className="card p-4" style={{ backgroundColor: '#F5F5DC', borderRadius: '10px' }}>
          <h2 className="mb-4" style={{ color: '#3B2F2F' }}>Create Card</h2>
          <FormComponent
            front={front}
            back={back}
            onFrontChange={handleFrontChange}
            onBackChange={handleBackChange}
            onSubmit={handleSaveButton}
            onCancel={() => navigate(`/decks/${deckId}`)} // Cancel button redirects back to deck view
            isEditMode={false} // Indicate that we're creating, not editing
          />
        </div>
      </div>
    </>
  );
}

export default AddCard;
